mapper.insert:
insert into BookBoard
(
bbno
, id
, address
, title
, content

	, originalfile
	, savedfile

, isbn
, thumbnail
, bookTitle
, author
, publisher
, price
, amount

) values (
bookBoard_seq.nextval
, #{id}
, #{address}
, #{title}
,
#{content}

	, #{originalfile}
	, #{savedfile}

, #{isbn}
, #{thumbnail}
, #{bookTitle}
, #{author}
, #{publisher}
,
#{price}
, #{amount}
)

mapper.delete:
delete from BookBoard
where
bbno like #{bbno}
