mapper.insert:
insert into BookBoard
(
bbno
, id
, address
, title
, content

	, originalfile
	, savedfile

, isbn
, thumbnail
, bookTitle
, author
, publisher
, price
, amount

) values
(
bookBoard_seq.nextval
, #{id}
, #{address}
, #{title}
,
#{content}

	, #{originalfile}
	, #{savedfile}

, #{isbn}
, #{thumbnail}
, #{bookTitle}
, #{author}
, #{publisher}
,
#{price}
, #{amount}
)

mapper.update:
update BookBoard
set
title
= #{title}
, content = #{content}
, address = #{address}
, isbn = #{isbn}
, thumbnail = #{thumbnail}
, bookTitle = #{bookTitle}
, author =
#{author}
, publisher = #{publisher}
, price = #{price}
, amount =
#{amount}
where
bbno = #{bbno}
and
id = #{id}

mapper.delete:
delete from BookBoard
where
bbno like #{bbno}

delete from like_board
where
id = #{id} and bno = #{boardnum}
