mapper.insert:
insert into MEMBER
	(
		uno, id, pw, name, email, phone, address, address_detail

		, originalfile
		, savedfile

	)
values
	(
		member_seq.nextval, #{id}, #{pw}, #{name}, #{email}, #{phone}, #{address}, #{address_detail}

		, #{originalfile}
		, #{savedfile}

	)

mapper.update:
update 
	member
set
	name = #{name}
	, email = #{email}
	, phone = #{phone}
	, address = #{address}
	, address_detail = #{address_detail}

	, pw = #{pw}

where
	id = #{id}
