mapper.insert:
insert into TradeBoard
(
bbno
, id
, address
, title
, content

	, originalfile
	, savedfile

, isbn
, thumbnail
, bookTitle
, author
, publisher
, amount

) values
(
tradeBoard_seq.nextval
, #{id}
, #{address}
, #{title}
,
#{content}

	, #{originalfile}
	, #{savedfile}

, #{isbn}
, #{thumbnail}
, #{bookTitle}
, #{author}
, #{publisher}
, #{amount}
)

mapper.update:
update TradeBoard
set
title= #{title}
, content = #{content}
, address = #{address}
, isbn = #{isbn}
, thumbnail = #{thumbnail}
, bookTitle = #{bookTitle}
, author = #{author}
, publisher = #{publisher}
, amount = #{amount}
where
bbno = #{bbno}
and
id = #{id}

mapper.delete:
delete from tradeBoard
where
bbno like #{bbno}

delete from like_board
where
id = #{id} and bno = #{boardnum}
